import { useState, useEffect } from "react";

const useFetch = (url) => {
  const [data, setData] = useState(null);

  useEffect(async () => {
    async function fetchdata() {
      await fetch(url)
        .then((res) => res.json())
        .then((data) => setData(data));
    }
  }, [url]);

  return [data];
};

export default useFetch;
