import { Post } from "../Post/Post";
import "./Blogs.css";
import { useState, useEffect } from "react";

export const Blogs = () => {
  const [blogs, setBlogs] = useState([]);
  const getPost = async () => {
    const result = await fetch("http://localhost:5002/api/posts/allposts");
    setBlogs(result);
  };

  useEffect(() => {
    getPost();
  }, [0]);
  return (
    <div className="Blogs">
      {blogs.map((index) => (
        <Post
          key={index._id}
          img={index.image}
          title={index.title}
          createdAt={index.createdAt}
          description={index.Desc}
        />
      ))}
    </div>
  );
};
