import { Post } from "../Post/Post";
import "./Blogs.css";
import { useState, useEffect } from "react";

export const Blogs = () => {
  const [data, setData] = useState([]);
  const getData = async () => {
    let result = await fetch("http://localhost:5002/api/posts/allposts");
    result = await result.json();
    // setData(result);
  };

  useEffect(() => {
    getData();
  });

  return (
    <div className="Blogs">
      {data &&
        data.Map((index) => {
          return (
            <Post
              key={index.id}
              title={index.title}
              img={index.image}
              description={index.Desc}
            />
          );
        })}
    </div>
  );
};
